    private void jbCrearActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbCrearActionPerformed
        
        //capturando la informacion de los empleados de los objetos del formulario
        String cod = txtCodigo.getText();
        String nom = txtNombre.getText();
        String seguro = cbTiposeguro.getSelectedItem().toString();
        double sueldo = Double.parseDouble(txtSueldoBase.getText());
        double h_extras = Double.parseDouble(txtHExtras.getText());
        String area = cbArea.getSelectedItem().toString();
        
        //creando el objeto empleado de la clase empleados
        e = new Empleado(cod, nom, area, sueldo, h_extras, seguro);
    }//GEN-LAST:event_jbCrearActionPerformed

    private void jbMostrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbMostrarActionPerformed
        
        //extrayendo la informacion de los atributos y metodos de la clase
        txtArea.append("Codigo              : " +e.codigo+"\n");
        txtArea.append("Nombre              : " +e.nombre+"\n");
        txtArea.append("Seguro              : " +e.tip_seg+"\n");
        txtArea.append("Sueldo Base         : " +e.sueldoBase+"\n");
        txtArea.append("Horas Extras        : " +e.horasExtras+"\n");
        txtArea.append("Area                : " +e.area+"\n");
        txtArea.append("Monto Extra         : " +e.montoHExtras()+"\n");
        txtArea.append("Monto Seguro        : " +e.montoSeguro()+"\n");
        txtArea.append("Monto Essalud       : " +e.montoEssalud()+"\n");
        txtArea.append("Monto de Descuentos : " +e.montoDescuentos()+"\n");
        txtArea.append("Sueldo Bruto        : " +e.sueldoBruto()+"\n");
        txtArea.append("Sueldoo Neto        : " +e.sueldoNeto()+"\n");
        
    }//GEN-LAST:event_jbMostrarActionPerformed

    private void jbLimpiarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbLimpiarActionPerformed
        //limpiando los objetos
        txtCodigo.setText("");
        txtNombre.setText("");
        cbTiposeguro.setSelectedIndex(0);
        txtSueldoBase.setText("");
        txtHExtras.setText("");
        cbArea.setSelectedIndex(0);
        txtArea.setText("");
        txtCodigo.requestFocus();
    }//GEN-LAST:event_jbLimpiarActionPerformed

